package com.gongjin.commom;import java.io.Serializable;import java.util.List;import com.google.common.collect.Lists;import lombok.Data;/** * 前端返回响应基类 *  * @title * @author 龚进 * @date 2017年9月4日 * @version 1.0 */@Datapublic class MessageResp<T> implements Serializable{	/**	 * 	 */	private static final long serialVersionUID = -1282981091525006494L;	/**	 * 操作结果 true:成功 false:失败	 */	private String result = Boolean.TRUE.toString();	/**	 * 结果描述	 */	private String resultDesc = "操作成功";	/**	 * 结果对象	 */	private T data;	/**	 * 状态码	 */	private String statusCode;		/**	 * 分页对象	 */	private PageBean pageBean = PageBean.DEFAULT;	/**	 * 设置返回结果为false	 */	public void setResultToFalse() {		result = Boolean.FALSE.toString();	}	public List<T> toList(T data) {		List<T> list = Lists.newArrayList();		list.add(data);		return list;	}}