package com.gongjin.commom;import java.io.Serializable;import java.util.List;import com.google.common.collect.Lists;/** * 前端返回响应基类 *  * @title * @author 龚进 * @date 2017年9月4日 * @version 1.0 */public class MessageResp<T> implements Serializable{	/**	 * 	 */	private static final long serialVersionUID = -1282981091525006494L;	/**	 * 操作结果 true:成功 false:失败	 */	private String result = Boolean.TRUE.toString();	/**	 * 结果描述	 */	private String resultDesc = "操作成功";	/**	 * 结果对象	 */	private T data;	/**	 * 状态码	 */	private String statusCode;		/**	 * 设置返回结果为false	 */	public void setResultToFalse() {		result = Boolean.FALSE.toString();	}	public List<T> toList(T data) {		List<T> list = Lists.newArrayList();		list.add(data);		return list;	}	public T getData() {		return data;	}	public void setData(T data) {		this.data = data;	}	public String getResult() {		return result;	}	public void setResult(String result) {		this.result = result;	}	public String getResultDesc() {		return resultDesc;	}	public void setResultDesc(String resultDesc) {		this.resultDesc = resultDesc;	}	public String getStatusCode() {		return statusCode;	}	public void setStatusCode(String statusCode) {		this.statusCode = statusCode;	}}